import React from "react";
import './contact.css';
/*import{ init } from 'emailjs-com';*/
import { motion } from 'framer-motion'

const Contact=()=>{ 
  const contact_variants = {
    hidden: {
        opacity: 0
    },
    visible: {
        opacity: 1,
        transition: {
            delay: 0.2, duration: 0.6,
        }
    },
    exit: {
        opacity: 0,
        transition: {
            ease: 'easeInOut'
        }
    }
}

    return (
      <motion.div className="container resume"

      variants={formation_variants}
      initial="hidden"
      animate="visible"
      exit="exit"

     >



      <div className="formContact">
      <form className="contact-form">
        <h2 className="contact-form-title">Si mon profil vous interesse, contactez moi via ce formulaire, je me ferai une joie de vous répondre dans les plus brefs délais </h2>
        <div className="form-content">
          <input
            type="text"
            id="name"
            name="name"
            required
            onChange={handleChange.bind(this)}
            placeholder="Nom *"
            value={name}
            autoComplete="off"
          />
          <input
            type="text"
            id="company"
            name="company"
            onChange={handleChange.bind(this)}
            placeholder="Entreprise"
            value={company}
          />
          <input
            type="text"
            id="phone"
            name="phone"
            onChange={handleChange.bind(this)}
            placeholder="Téléphone"
            value={phone}
          />
          <div className="email-content">
            <label id="not-mail">Email non valide</label>
            <input
              type="mail"
              id="email"
              name="email"
              required
              onChange={handleChange.bind(this)}
              placeholder="Email *"
              value={email}
              autoComplete="off"
            />
          </div>
          <textarea
            id="message"
            name="message"
            onChange={handleChange.bind(this)}
            placeholder="Message *"
            required
            value={message}
          />
        </div>
        <input className="formButton" type="button" value="Envoyer" onClick={handleSubmit.bind(this)} />
        <div className="end.form-message"></div>
      </form>
      </div>
      </motion.div>
    ); 
  }

export default Contact;
