import React from 'react'

import linkedin from '../assets/icons/lkd.jpeg'
import github from '../assets/icons/github.svg'
import pin from '../assets/icons/pin.jpeg'
import tie from '../assets/icons/tie.svg'
import mightycoder from '../assets/mightycoder.svg'
import resume from '../assets/resume.pdf'
import { motion } from 'framer-motion'

const Sidebar = () => {
    const handleEmailMe = () => {
        window.open("mailto:codesumax@gmail.com")
    }
    const sidebar_variant = {
        hidden: {
            x: '-20vw',

        },
        visible: {
            x: 0,

            transition: {
                delay: 0.1, duration: 0.5, type: 'spring'
            }
        }
    }


    return (
        <motion.div className="sidebar"
            variants={sidebar_variant}
            initial='hidden'
            animate="visible"

        >
            <img src={mightycoder} alt="avatar" className="sidebar__avatar" />
            <div className="sidebar__name">Eve <span>JOURDAN</span> </div>
            <div className="sidebar__item sidebar__title">Developpeuse Webr</div>
            <a href={resume} download="resume.pdf">
                <div className="sidebar__item sidebar__resume">
                    <img src={tie} alt="resume" className="sidebar__icon" />Telecharger mon CV
                </div>
            </a>

            <div className="sidebar__contact">
                <div className="sidebar__item sidebar__github">
                    <a href="!#"><img src={github} alt="github" className="sidebar__icon mr-3" />github</a>
                    <a href="!#"><img src={linkedin} alt="linkedin" className="sidebar__icon mr-3" />linkedin</a>

                </div>
                <div className="sidebar__location">
                    <img src={pin} alt="location" className="sidebar__icon mr-3" />
                    Aix - Marseille</div>
            </div>
            <div className="sidebar__item sidebar__email" onClick={handleEmailMe}>email me</div>
        </motion.div>
    )
}

export default Sidebar;
