import React , {useEffect } from 'react';
import Sidebar from './components/Sidebar'
import Navbar from './components/Navbar';
import { Route, Redirect, Switch, useLocation, useHistory } from 'react-router-dom';
import About from './components/about/About';
import Formations from './components/Formations';
import Projets from './components/Projets';
import Competences from './components/Competences';
import { AnimatePresence } from 'framer-motion'
import Contact from './components/contact/Contact';
function App() {

  const location = useLocation();
  let history = useHistory();

  useEffect(() => {
    const handleScrollToElement = (e) => {
      console.log(e.wheelDeltaY);
      const url = window.location.origin + "/";

      const wheelRouter = (after, before) => {
        if (e.wheelDeltaY < 0) {
          setTimeout(() => {
            history.push(after);
          }, 500);
        } else if (e.wheelDeltaY > 0) {
          setTimeout(() => {
            history.push(before);
          }, 500);
        }
      };

      switch (window.location.href.toString()) {
        case url:
          if (e.wheelDeltaY < 0) {
            setTimeout(() => {
              history.push("about");
            }, 500);
          }
          break;
        case url + "about":
          wheelRouter("about", "");
          break;
        case url + "formation":
          wheelRouter("formatio", "projet-1");
          break;
        case url + "projet-3":
          wheelRouter("projet-4", "projet-2");
          break;
        case url + "projet-4":
          wheelRouter("contact", "projet-3");
          break;
        case url + "contact":
          if (e.wheelDeltaY > 0) {
            setTimeout(() => {
              history.push("projet-4");
            }, 500);
          }
          break;
        default:
          console.log("nothing");
      }
    };
    window.addEventListener("wheel", handleScrollToElement);
  }, [history]);

  return (

    <div className="app">
      <div className="container app__container">
        <div className="row app__row">
          <div className="col-lg-3">
            <Sidebar />
          </div>
          <div className="col-lg-9 app__main-content">
            {/* navbar */}
            <Navbar />
            <AnimatePresence exitBeforeEnter>
              <Switch location={location} key={location.key}>
                <Route exact path="/">
                  <About />
                </Route>
                <Route path="/formations" component={Formations} />                 
                <Route path="/competences" component={Competences} />
                <Route path="/projets" component={Projets} />
                <Route path="/contact" exact render={(props) => (<Contact test="hi" {...props}/>)} />
                <Redirect to="/" />               
              </Switch>
            </AnimatePresence>
          </div>
        </div>
      </div>
    </div>

  );
}

export default App;
